mapper.insert:

  SELECT LAST_INSERT_ID()

insert into sys_config (value, set_time, set_by
  )
values (#{value,jdbcType=VARCHAR}, #{setTime,jdbcType=TIMESTAMP}, #{setBy,jdbcType=VARCHAR}
  )

mapper.update:
update sys_config
set value = #{value,jdbcType=VARCHAR},
  set_time = #{setTime,jdbcType=TIMESTAMP},
  set_by = #{setBy,jdbcType=VARCHAR}
where variable = #{variable,jdbcType=VARCHAR}

mapper.select:
select variable, value, set_time, set_by
from sys_config
where variable = #{variable,jdbcType=VARCHAR}
